mapper.insert:
delete from sys_role_user where user_id=#{userId};
insert into sys_role_user(user_id,role_id,create_time)
values (#{userId},#{roleId},now());

mapper.select:
select id from sys_user t
where t.username = #{username}
and t.password = #{password}

SELECT t.id FROM sys_user t
WHERE t.username = #{username}
AND t.`password` = #{password}
AND t.`status` = 1

select count(1) from sys_user a LEFT JOIN sys_role_user b ON a.id = b.user_id LEFT JOIN sys_role c ON c.id = b.role_id LEFT JOIN sys_organization f ON a.org_code = f.`code` where a.del_flag = 0
and a.id = #{userId}
and a.org_code = #{orgCode}
and a.org_code like CONCAT(#{orgCode},'%')
and a.username like concat(#{userName},'%')
and c.`name` like concat(#{roleName},'%')
and f.`name` like concat(#{orgName},'%')
and a.`status` = #{status}

SELECT a.id, a.username, c.id AS roleId, c.`name` AS roleName, a.`status`, a.org_code AS orgCode, f.`name` AS orgName, DATE_FORMAT(a.vaild_time,'%Y-%m-%d %H:%i:%s') AS vaildTime, DATE_FORMAT(a.start_time,'%Y-%m-%d %H:%i:%s') AS startTime, c.init_role AS initRole, c.super_admin AS superAdmin, a.is_desensitization AS isDesensitization, DATE_FORMAT(a.create_time,'%Y-%m-%d %H:%i:%s') AS createTime, e.username AS createName, a.inten_label AS intenLabel FROM sys_user a LEFT JOIN sys_role_user b ON a.id = b.user_id LEFT JOIN sys_role c ON c.id = b.role_id LEFT JOIN sys_user e ON e.id = a.create_id LEFT JOIN sys_organization f ON a.org_code = f.`code` WHERE a.del_flag = 0
and a.id = #{userId}
and a.org_code = #{orgCode}
and a.org_code like CONCAT(#{orgCode},'%')
and a.username like concat(#{userName},'%')
and f.`name` like concat(#{orgName},'%')
and c.`name` like concat(#{roleName},'%')
and a.`status` = #{status}
order by a.create_time desc LIMIT ${(pageNo-1)*pageSize},#{pageSize}

select a.id, a.username from sys_user a where a.del_flag = 0
and a.create_id = #{userId}
and a.org_code = #{orgCode}
and a.org_code like CONCAT(#{orgCode},'%')
and a.username like concat('%',#{param.userName},'%')
order by a.create_time desc

mapper.insert:
delete from sys_user_ext where user_id=#{userId};
insert into sys_user_ext(user_id,create_time,update_time)
values (#{userId},now(),now());

mapper.select:
SELECT * FROM sys_user a
LEFT JOIN sys_user_ext b ON a.id = b.user_id
WHERE b.wechat_openid = #{openId}
