mapper.insert:
delete from sys_menu_role where role_id=#{roleId};
insert into sys_menu_role(role_id,menu_id,create_time,update_time) values

  (#{roleId},#{item},now(),now())

;

mapper.select:
select a.id,a.name,a.desc,DATE_FORMAT(a.create_time,'%Y-%m-%d %H:%i:%s') as createTime,b.username as createName,a.init_role as initRole from sys_role a LEFT JOIN sys_user b on a.create_id =b.id where a.del_flag=0
and name like concat('%',#{roleName},'%')
order by a.create_time desc limit ${(pageNo-1)*pageSize}, #{pageSize}

select count(1)>0 from sys_role where name = #{name}
and org_code = #{orgCode} 

	and id!=#{id}
