package com.guiji.clm.service.voip;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.scheduling.annotation.Async;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import com.guiji.clm.dao.VoipGwPortHisMapper;import com.guiji.clm.dao.entity.VoipGwPort;import com.guiji.clm.dao.entity.VoipGwPortHis;import com.guiji.utils.BeanUtil;import com.guiji.utils.DateUtil;import lombok.extern.slf4j.Slf4j;/** * @Description: 语音网关端口配置服务* @Author: weiyunbo* @date 2019年1月23日 上午10:32:41 * @version V1.0  */@Slf4j@Servicepublic class VoipGwPortHisHisService {	@Autowired	VoipGwPortHisMapper voipGwPortHisMapper;			/**	 * 异步记录下变更历史	 * @param VoipGwPort	 * @return	 */	@Async	@Transactional	public void asyncSaveGwPortHis(VoipGwPort voipGwPort) {		if(voipGwPort!=null) {			VoipGwPortHis his = new VoipGwPortHis();			BeanUtil.copyProperties(voipGwPort, his);			his.setId(null);			his.setCrtTime(DateUtil.getCurrent4Time());			voipGwPortHisMapper.insert(his);		}	}	}